); sqls
[ 'DROP TABLE IF EXISTS `user`', '\n' + '\n' + 'CREATE TABLE `user` (\n' + ' id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, \n' + ' name TEXT NOT NULL,\n' + ' age INTEGER NOT NULL\n' + ')'
]
start run:
